INSERT INTO dialogue( id, scene_id, character_id, texte, action ) VALUES ( 7, 1, 1, 'Merci pour vos mots encourageants', 'en souriant' ); 
INSERT INTO dialogue( id, scene_id, character_id, texte, action ) VALUES ( 8, 1, 6, 'Bonne chance dans votre recherche, et n''oubliez pas de profiter de la beaute qui vous entoure, meme lorsque les choses sont difficiles', 'en souriant' ); 
INSERT INTO dialogue( id, scene_id, character_id, texte, action ) VALUES ( 9, 1, 1, 'Merci. Je vais essayer de garder cela a l''esprit. Bonne journee a vous', 'en se levant' ); 
INSERT INTO dialogue( id, scene_id, character_id, texte, action ) VALUES ( 10, 1, 6, 'Bonne journee a vous aussi', 'en souriant' ); 
