--view
CREATE or REPLACE view v_top_planning as
	select filmset.*
	from filmset 
	join scene on scene.filmset_id=filmset.id ;

CREATE or REPLACE view v_planning as 
	select planning.*,name,type_id,x,y,title,global_action,time_start,time_end,estimated_time,filmset_id,film_id,status
	from scene join filmset on scene.filmset_id=filmset.id 
	join planning on scene.id=planning.scene_id;

CREATE OR REPLACE view v_actor_scene as 
	select scene.*,character_id,actor_id 
	from scene 
	join dialogue on scene.id=dialogue.scene_id 
	join character on dialogue.character_id=character.id 
	join actor on character.actor_id=actor.id;

CREATE OR REPLACE VIEW v_scene_actor as
	SELECT s.id as scene_id, c.id as actor_id
	from scene s, dialogue d, character c
	where s.id = d.scene_id and c.id = d.character_id;

